import React, { useState } from 'react';
import './DashboardPage.css';

// --- Report Modal ---
function ReportModal({ onClose }) {
  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h3 className="modal-title">Report</h3>
        <div className="modal-message">
          <p>
            Ce rapport signale cette news comme potentiellement fausse ou problématique. Merci de contribuer à la fiabilité de la plateforme.
          </p>
        </div>
        <button className="btn btn-exit" onClick={onClose}>Exit</button>
      </div>
    </div>
  );
}

// --- Community Survey Modal ---
const surveyQuestions = [
  'Do you think this information is reliable?',
  'Have you seen this information elsewhere?',
  'Does this information impact your opinion?',
];

function ExampleGraph() {
  return (
    <div className="survey-graph">
      <div className="bar-yes" style={{width: '70%'}}>Yes: 70%</div>
      <div className="bar-no" style={{width: '30%'}}>No: 30%</div>
    </div>
  );
}

function CommunitySurveyModal({ onClose, hasAnswered, onFinish, waitingTimeLeft, showFinalScore }) {
  const [step, setStep] = useState(0);
  const [selected, setSelected] = useState(null);
  const [finished, setFinished] = useState(false);

  // Si déjà répondu, afficher la page d'attente ou le score final
  if (hasAnswered && !finished) {
    return (
      <div className="modal-overlay">
        <div className="modal-content">
          <h3 className="modal-title">Community Survey</h3>
          <div className="modal-message">
            <p>Merci d'avoir répondu à l'enquête communautaire !</p>
            <p>En attente des réponses de la communauté...</p>
            <p>Temps restant : {waitingTimeLeft} min</p>
          </div>
          <button className="btn btn-exit" onClick={onFinish}>Exit</button>
        </div>
      </div>
    );
  }
  if (hasAnswered && finished) {
    return (
      <div className="modal-overlay">
        <div className="modal-content">
          <h3 className="modal-title">Community Survey</h3>
          <div className="modal-message">
            <p>Score final de la communauté :</p>
            <span className="report-stars">
              {Array.from({ length: 5 }).map((_, i) => (
                <span key={i} className={i < 4 ? 'star filled' : 'star'}>★</span>
              ))}
              <span className="score-text">4/5</span>
            </span>
          </div>
          <button className="btn btn-exit" onClick={onFinish}>Exit</button>
        </div>
      </div>
    );
  }

  // Dernière question avec graphe
  if (step === 3) {
    return (
      <div className="modal-overlay">
        <div className="modal-content">
          <h3 className="modal-title">Community Survey</h3>
          <ExampleGraph />
          <div className="survey-question">Do you really think that it is a fake news?</div>
          <div className="survey-options">
            <label className="survey-option">
              <input type="checkbox" checked={selected === 'yes'} onChange={() => setSelected('yes')} /> <span>Yes</span>
            </label>
            <label className="survey-option">
              <input type="checkbox" checked={selected === 'no'} onChange={() => setSelected('no')} /> <span>No</span>
            </label>
          </div>
          <div className="survey-actions">
            <button className="btn btn-primary" disabled={!selected} onClick={() => { setFinished(true); onFinish(); }}>Finish</button>
          </div>
          <button className="btn btn-exit" onClick={onFinish}>Exit</button>
        </div>
      </div>
    );
  }

  // Questions classiques
  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h3 className="modal-title">Community Survey</h3>
        <div className="survey-question">{surveyQuestions[step]}</div>
        <div className="survey-options">
          <label className="survey-option">
            <input type="checkbox" checked={selected === 'yes'} onChange={() => setSelected('yes')} /> <span>Yes</span>
          </label>
          <label className="survey-option">
            <input type="checkbox" checked={selected === 'no'} onChange={() => setSelected('no')} /> <span>No</span>
          </label>
        </div>
        <div className="survey-actions">
          <button className="btn btn-primary" disabled={!selected} onClick={() => { setStep(step + 1); setSelected(null); }}>Next</button>
        </div>
        <button className="btn btn-exit" onClick={onFinish}>Exit</button>
      </div>
    </div>
  );
}

// --- Victim Report Modal ---
function VictimReportModal({ onClose }) {
  const interviewText = `
    "Je tiens à clarifier que les informations diffusées à mon sujet sont totalement fausses. J'ai pris la parole publiquement pour rétablir la vérité et je remercie tous ceux qui m'ont soutenu durant cette période difficile."
  `;
  const victimMedia = '/assets/victim-interview.jpg';
  return (
    <div className="modal-overlay">
      <div className="modal-content victim-modal">
        <h3 className="modal-title">Victim Report</h3>
        <div className="victim-modal-body">
          <div className="victim-interview-text">
            {interviewText}
          </div>
          <div className="victim-media">
            <img src={victimMedia} alt="Victim public statement" />
          </div>
        </div>
        <button className="btn btn-exit" onClick={onClose}>Exit</button>
      </div>
    </div>
  );
}

// --- Last Words Author Modal ---
function LastWordsAuthorModal({ onClose }) {
  const authorText = `
    "Nous avons publié cette information sans vérifier sa véracité. Nous regrettons l'impact que cela a pu avoir et présentons nos excuses à la personne concernée."
  `;
  const authorMedia = '/assets/author-statement.jpg';
  return (
    <div className="modal-overlay">
      <div className="modal-content author-modal">
        <h3 className="modal-title">Last Words Author</h3>
        <div className="author-modal-body">
          <div className="author-text">
            {authorText}
          </div>
          <div className="author-media">
            <img src={authorMedia} alt="Author public statement" />
          </div>
        </div>
        <button className="btn btn-exit" onClick={onClose}>Exit</button>
      </div>
    </div>
  );
}

// --- Bloc d'information central ---
const NewsInfoBlock = ({ description, link, sources, date }) => (
  <div className="news-info-block">
    <p className="news-description" title={description}>{description}</p>
    <a href={link} target="_blank" rel="noopener noreferrer" className="news-link">{link}</a>
    <div className="news-sources">
      {sources.map((src, idx) => (
        <div key={idx} className="news-source-item">
          <span className="news-date">{date}</span>
          <a href={src} target="_blank" rel="noopener noreferrer" className="news-source-link">{src}</a>
        </div>
      ))}
    </div>
  </div>
);

// --- Composant d'affichage des étoiles à côté d'un bouton ou en note finale
function StarScore({ score, maxScore = 5 }) {
  return (
    <span className="report-stars">
      {Array.from({ length: maxScore }).map((_, i) => (
        <span key={i} className={i < score ? 'star filled' : 'star'}>★</span>
      ))}
      <span className="score-text">{score}/{maxScore}</span>
    </span>
  );
}

// --- NewsActions avec notes à côté de chaque bouton
const NewsActions = ({
  onReport, onCommunitySurvey, onVictimReport, onLastWordsAuthor,
  scores
}) => (
  <div className="news-actions">
    <div className="action-row">
      <button className="btn btn-report" onClick={onReport}>Report</button>
      <StarScore score={scores.report} />
    </div>
    <div className="action-row">
      <button className="btn btn-community" onClick={onCommunitySurvey}>Community Survey</button>
      <StarScore score={scores.community} />
    </div>
    <div className="action-row">
      <button className="btn btn-victim" onClick={onVictimReport}>Victim Report</button>
      <StarScore score={scores.victim} />
    </div>
    <div className="action-row">
      <button className="btn btn-lastwords" onClick={onLastWordsAuthor}>Last Words Author</button>
      <StarScore score={scores.author} />
    </div>
  </div>
);

// --- NewsCard principal ---
const NewsCard = ({ imageUrl, description, link, sources, date, onReport, onCommunitySurvey, onVictimReport, onLastWordsAuthor, scores }) => (
  <div className="news-card">
    <img src={imageUrl} alt="news" className="news-image" />
    <NewsInfoBlock description={description} link={link} sources={sources} date={date} />
    <NewsActions
      onReport={onReport}
      onCommunitySurvey={onCommunitySurvey}
      onVictimReport={onVictimReport}
      onLastWordsAuthor={onLastWordsAuthor}
      scores={scores}
    />
  </div>
);

// --- DashboardPage ---
export default function DashboardPage() {
  // State pour afficher les modales
  const [showReport, setShowReport] = useState(false);
  const [showSurvey, setShowSurvey] = useState(false);
  const [showVictim, setShowVictim] = useState(false);
  const [showAuthor, setShowAuthor] = useState(false);
  // State pour Community Survey (simulateur)
  const [hasAnsweredSurvey, setHasAnsweredSurvey] = useState(false);
  const [waitingTimeLeft, setWaitingTimeLeft] = useState(60); // minutes
  const [showFinalCommunityScore, setShowFinalCommunityScore] = useState(false);

  // Scores pour chaque bouton (exemple)
  const scores = {
    report: 2,
    community: 4,
    victim: 3,
    author: 1
  };

  return (
    <div className="dashboard-page">
      <h2>Tableau de bord</h2>
      <NewsCard
        {...news}
        onReport={() => setShowReport(true)}
        onCommunitySurvey={() => setShowSurvey(true)}
        onVictimReport={() => setShowVictim(true)}
        onLastWordsAuthor={() => setShowAuthor(true)}
        scores={scores}
      />
      {/* Final grade sous les boutons */}
      <div className="final-grade">
        <span>Final grade: </span>
        <StarScore score={4} />
      </div>
      {showReport && <ReportModal onClose={() => setShowReport(false)} />}
      {showSurvey && (
        <CommunitySurveyModal
          onClose={() => setShowSurvey(false)}
          hasAnswered={hasAnsweredSurvey}
          onFinish={() => {
            if (!hasAnsweredSurvey) {
              setHasAnsweredSurvey(true);
              setTimeout(() => setShowFinalCommunityScore(true), 3600000); // 1h timer
            } else {
              setShowSurvey(false);
            }
          }}
          waitingTimeLeft={waitingTimeLeft}
          showFinalScore={showFinalCommunityScore}
        />
      )}
      {showVictim && <VictimReportModal onClose={() => setShowVictim(false)} />}
      {showAuthor && <LastWordsAuthorModal onClose={() => setShowAuthor(false)} />}
    </div>
  );
}
