import React, { useState } from 'react';
import './DashboardPage.css';

/**
 * Composant modulaire pour la fenêtre modale de report
 */
function ReportModal({ onClose }) {
  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h3 className="modal-title">Report</h3>
        <div className="modal-message">
          <p>
            Ce rapport signale cette news comme potentiellement fausse ou problématique. Merci de contribuer à la fiabilité de la plateforme.
          </p>
        </div>
        <button className="btn btn-exit" onClick={onClose}>Exit</button>
      </div>
    </div>
  );
}

// --- Community Survey Modal ---
const surveyQuestions = [
  'Do you think this information is reliable?',
  'Have you seen this information elsewhere?',
  'Does this information impact your opinion?',
];

function ExampleGraph() {
  return (
    <div className="survey-graph">
      <div className="bar-yes" style={{width: '70%'}}>Yes: 70%</div>
      <div className="bar-no" style={{width: '30%'}}>No: 30%</div>
    </div>
  );
}

function CommunitySurveyModal({ onClose, onSurveyComplete, surveyStartTime, surveyDurationMs }) {
  const [step, setStep] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [selected, setSelected] = useState(null);
  const [waiting, setWaiting] = useState(false);
  const [timeLeft, setTimeLeft] = useState(0);

  // Calcul du temps restant
  React.useEffect(() => {
    if (waiting) {
      const interval = setInterval(() => {
        const now = Date.now();
        const end = surveyStartTime + surveyDurationMs;
        const left = Math.max(0, end - now);
        setTimeLeft(left);
        if (left <= 0) {
          clearInterval(interval);
          onSurveyComplete();
        }
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [waiting, surveyStartTime, surveyDurationMs, onSurveyComplete]);

  // Si le timer est déjà en cours (utilisateur revient avant la fin)
  React.useEffect(() => {
    if (surveyStartTime && Date.now() < surveyStartTime + surveyDurationMs) {
      setWaiting(true);
    }
  }, [surveyStartTime, surveyDurationMs]);

  // Handle Yes/No selection
  const handleSelect = (value) => setSelected(value);
  // Handle Next (Yes/No) click
  const handleNext = (answer) => {
    setAnswers([...answers, answer]);
    setSelected(null);
    setStep(step + 1);
  };
  // Handle Exit
  const handleExit = () => {
    setStep(0);
    setAnswers([]);
    setSelected(null);
    onClose();
  };

  // Si on attend la fin du timer
  if (waiting) {
    const seconds = Math.ceil(timeLeft / 1000) % 60;
    const minutes = Math.floor(timeLeft / 60000) % 60;
    const hours = Math.floor(timeLeft / 3600000);
    return (
      <div className="modal-overlay">
        <div className="modal-content">
          <h3 className="modal-title">Community Survey</h3>
          <div className="modal-message">
            <p>Merci d'avoir répondu à l'enquête communautaire !</p>
            <p>Résultats disponibles dans&nbsp;
              <b>{hours}h {minutes}m {seconds}s</b>
            </p>
          </div>
          <button className="btn btn-exit" onClick={handleExit}>Exit</button>
        </div>
      </div>
    );
  }

  // Standard question modal
  if (step < surveyQuestions.length) {
    return (
      <div className="modal-overlay">
        <div className="modal-content">
          <h3 className="modal-title">Community Survey</h3>
          <div className="survey-question">{surveyQuestions[step]}</div>
          <div className="survey-options">
            <label className="survey-option">
              <input type="checkbox" checked={selected === 'yes'} onChange={() => handleSelect('yes')} /> <span>Yes</span>
            </label>
            <label className="survey-option">
              <input type="checkbox" checked={selected === 'no'} onChange={() => handleSelect('no')} /> <span>No</span>
            </label>
          </div>
          <div className="survey-actions">
            <button className="btn btn-primary" disabled={!selected} onClick={() => handleNext(selected)}>{step === surveyQuestions.length - 1 ? 'Finish' : 'Next'}</button>
          </div>
          <button className="btn btn-exit" onClick={handleExit}>Exit</button>
        </div>
      </div>
    );
  }

  // Après avoir répondu à toutes les questions, on passe à la page d'attente
  if (!waiting) {
    setWaiting(true);
    if (!surveyStartTime) {
      window.localStorage.setItem('communitySurveyStart', Date.now().toString());
    }
  }
  return null;
}

/**
 * Bloc d'information central : description tronquée, lien, sources et date
 */
const NewsInfoBlock = ({ description, link, sources, date }) => (
  <div className="news-info-block">
    {/* Description tronquée avec trois points si trop longue */}
    <p className="news-description" title={description}>{description}</p>
    {/* Lien de la fake news */}
    <a href={link} target="_blank" rel="noopener noreferrer" className="news-link">{link}</a>
    {/* Sources et date */}
    <div className="news-sources">
      {sources.map((src, idx) => (
        <div key={idx} className="news-source-item">
          <span className="news-date">{date}</span>
          <a href={src} target="_blank" rel="noopener noreferrer" className="news-source-link">{src}</a>
        </div>
      ))}
    </div>
  </div>
);

/**
 * Composant pour afficher une note sous forme d'étoiles
 */
function StarScore({ score, maxScore = 5 }) {
  return (
    <span className="star-score">
      {Array.from({ length: maxScore }).map((_, i) => (
        <span key={i} className={i < score ? 'star filled' : 'star'}>★</span>
      ))}
      <span className="score-text">{score}/{maxScore}</span>
    </span>
  );
}

/**
 * Bloc boutons verticaux, gère l'ouverture de la modale Report et l'affichage dynamique de la note
 */
const NewsActions = ({
  onReport,
  onCommunitySurvey,
  onTargetReport,
  onLastWordsAuthor,
  reportScore,
  showReportScore,
  communityScore,
  showCommunityScore,
  targetScore,
  showTargetScore,
  lastWordsScore,
  showLastWordsScore
}) => (
  <div className="news-actions">
    <div className="action-row">
      <button className="btn btn-report" onClick={onReport}>Report</button>
      {showReportScore && <StarScore score={reportScore} />}
    </div>
    <div className="action-row">
      <button className="btn btn-community" onClick={onCommunitySurvey}>Community Survey</button>
      {showCommunityScore && <StarScore score={communityScore} />}
    </div>
    <div className="action-row">
      <button className="btn btn-victim" onClick={onTargetReport}>Target Report</button>
      {showTargetScore && <StarScore score={targetScore} />}
    </div>
    <div className="action-row">
      <button className="btn btn-lastwords" onClick={onLastWordsAuthor}>Last Words Author</button>
      {showLastWordsScore && <StarScore score={lastWordsScore} />}
    </div>
  </div>
);

/**
 * Composant NewsCard : image | bloc info | boutons
 */
const NewsCard = ({ imageUrl, description, link, sources, date, ...actionsProps }) => (
  <div className="news-card">
    {/* Bloc image à gauche */}
    <img src={imageUrl} alt="news" className="news-image" />
    {/* Bloc central : description, lien, sources, date */}
    <NewsInfoBlock description={description} link={link} sources={sources} date={date} />
    {/* Bloc boutons à droite */}
    <NewsActions {...actionsProps} />
  </div>
);

// Page Tableau de bord : affiche une news fake
export default function DashboardPage() {
  // State pour afficher les modales
  const [showReport, setShowReport] = useState(false);
  const [showSurvey, setShowSurvey] = useState(false);
  const [showTarget, setShowTarget] = useState(false);
  const [showAuthor, setShowAuthor] = useState(false);
  // State pour afficher la note Report après le premier clic
  const [showReportScore, setShowReportScore] = useState(false);
  // State pour Community Survey
  const [showCommunityScore, setShowCommunityScore] = useState(false);
  const [surveyStartTime, setSurveyStartTime] = useState(() => {
    const stored = window.localStorage.getItem('communitySurveyStart');
    return stored ? parseInt(stored, 10) : null;
  });
  const surveyDurationMs = 60 * 60 * 1000; // 1h en ms
  const communityScore = 4; // Score à afficher après 1h

  // Callback quand le timer est fini
  const handleSurveyComplete = () => {
    setShowCommunityScore(true);
  };

  // Exemple de news unique
  const news = {
    imageUrl: '/assets/fake-news-example.jpg',
    description: "Cette fausse information a circulé massivement sur les réseaux sociaux en mai 2025. Elle a été partagée par de nombreux comptes influents et a généré beaucoup de réactions dans la communauté. Les vérifications ont montré qu'il s'agissait d'une manipulation.",
    link: 'https://www.example.com/fake-news',
    sources: [
      'https://www.source1.com/article',
      'https://www.source2.com/article'
    ],
    date: '17/05/2025',
  };

  return (
    <div className="dashboard-page">
      <h2>Tableau de bord</h2>
      <NewsCard
        {...news}
        onReport={() => { setShowReport(true); }}
        onCommunitySurvey={() => {
          setShowSurvey(true);
          if (!surveyStartTime) {
            const now = Date.now();
            setSurveyStartTime(now);
            window.localStorage.setItem('communitySurveyStart', now.toString());
          }
        }}
        onTargetReport={() => setShowTarget(true)}
        onLastWordsAuthor={() => setShowAuthor(true)}
        reportScore={reportScore}
        showReportScore={showReportScore}
        communityScore={communityScore}
        showCommunityScore={showCommunityScore}
        targetScore={targetScore}
        showTargetScore={true}
        lastWordsScore={lastWordsScore}
        showLastWordsScore={true}
      />
      {showReport && <ReportModal onClose={() => { setShowReport(false); setShowReportScore(true); }} />}
      {showSurvey && (
        <CommunitySurveyModal
          onClose={() => setShowSurvey(false)}
          onSurveyComplete={handleSurveyComplete}
          surveyStartTime={surveyStartTime}
          surveyDurationMs={surveyDurationMs}
        />
      )}
      {showTarget && <VictimReportModal onClose={() => setShowTarget(false)} />}
      {showAuthor && <LastWordsAuthorModal onClose={() => setShowAuthor(false)} />}
    </div>
  );
}

// --- Victim Report Modal ---
function VictimReportModal({ onClose }) {
  // Exemple de texte d'interview et vidéo/screenshot
  const interviewText = `
    "Je tiens à clarifier que les informations diffusées à mon sujet sont totalement fausses. J'ai pris la parole publiquement pour rétablir la vérité et je remercie tous ceux qui m'ont soutenu durant cette période difficile."
  `;
  const victimMedia = '/assets/victim-interview.jpg'; // Remplacer par une vidéo ou une image réelle si besoin

  return (
    <div className="modal-overlay">
      <div className="modal-content victim-modal">
        <h3 className="modal-title">Victim Report</h3>
        <div className="victim-modal-body">
          <div className="victim-interview-text">
            {interviewText}
          </div>
          <div className="victim-media">
            <img src={victimMedia} alt="Victim public statement" />
          </div>
        </div>
        <button className="btn btn-exit" onClick={onClose}>Exit</button>
      </div>
    </div>
  );
}

// --- Last Words Author Modal ---
function LastWordsAuthorModal({ onClose }) {
  // Exemple de texte et image de l'auteur de la fake news
  const authorText = `
    "Nous avons publié cette information sans vérifier sa véracité. Nous regrettons l'impact que cela a pu avoir et présentons nos excuses à la personne concernée."
  `;
  const authorMedia = '/assets/author-statement.jpg'; // Remplacer par une image réelle si besoin

  return (
    <div className="modal-overlay">
      <div className="modal-content author-modal">
        <h3 className="modal-title">Last Words Author</h3>
        <div className="author-modal-body">
          <div className="author-text">
            {authorText}
          </div>
          <div className="author-media">
            <img src={authorMedia} alt="Author public statement" />
          </div>
        </div>
        <button className="btn btn-exit" onClick={onClose}>Exit</button>
      </div>
    </div>
  );
}
