import React from 'react';
import './NewsDashboardPage.css';

/**
 * Composant NewsCard : affiche une news avec lien, description, image et actions
 */
const NewsCard = ({ link, description, imageUrl }) => (
  <div className="news-card">
    <div className="news-main">
      <a href={link} target="_blank" rel="noopener noreferrer" className="news-link">{link}</a>
      <p className="news-description">{description}</p>
      <img src={imageUrl} alt="news" className="news-image" />
    </div>
    <div className="news-actions">
      <button className="btn btn-report">Report</button>
      <button className="btn btn-community">Community Survey</button>
      <button className="btn btn-victim">Victim Report</button>
      <button className="btn btn-lastwords">Last Words Author</button>
    </div>
  </div>
);

/**
 * Page Tableau de bord des news
 * Affiche une liste de NewsCard (ici exemple statique)
 */
const NewsDashboardPage = () => {
  // Exemple de données de news
  const newsList = [
    {
      link: 'https://www.example.com/news1',
      description: 'Une nouvelle potentiellement virale sur la désinformation.',
      imageUrl: '/assets/news1.jpg',
    },
    {
      link: 'https://www.example.com/news2',
      description: 'Analyse d’une image suspecte partagée sur les réseaux.',
      imageUrl: '/assets/news2.jpg',
    },
  ];

  return (
    <div className="news-dashboard-page">
      <h2>Tableau de bord des News</h2>
      <div className="news-list">
        {newsList.map((news, idx) => (
          <NewsCard key={idx} {...news} />
        ))}
      </div>
    </div>
  );
};

export default NewsDashboardPage;
