import React, { useState } from 'react';
import './DashboardPage.css';

/**
 * Composant modulaire pour la fenêtre modale de report
 */
function ReportModal({ onClose, onValidate }) {
  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h3 className="modal-title">Report</h3>
        <div className="modal-message">
          <textarea
            className="report-textarea"
            value={"Ce contenu présente des signaux de désinformation détectés par l'IA. Merci de contribuer à la fiabilité de la plateforme."}
            readOnly
            rows={5}
          />
        </div>
        <button className="btn btn-primary" onClick={onValidate}>Valider</button>
        <button className="btn btn-exit" onClick={onClose}>Exit</button>
      </div>
    </div>
  );
}

// --- Community Survey Modal ---
const surveyQuestions = [
  'Do you think this information is reliable?',
  'Have you seen this information elsewhere?',
  'Does this information impact your opinion?',
  // The last question will be handled separately for the graph
];

// Example graph component for the last question
function ExampleGraph() {
  return (
    <div className="survey-graph">
      <div className="bar-yes" style={{width: '70%'}}>Yes: 70%</div>
      <div className="bar-no" style={{width: '30%'}}>No: 30%</div>
    </div>
  );
}

function CommunitySurveyModal({ onClose, onValidate }) {
  // State for current question and answers
  const [step, setStep] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [selected, setSelected] = useState(null);
  const maxScore = 5;
  const score = 4;

  // Handle Yes/No selection
  const handleSelect = (value) => setSelected(value);

  // Handle Next (Yes/No) click
  const handleNext = (answer) => {
    setAnswers([...answers, answer]);
    setSelected(null);
    setStep(step + 1);
  };

  // Last question and graph
  if (step === 3) {
    return (
      <div className="modal-overlay">
        <div className="modal-content">
          <h3 className="modal-title">Community Survey</h3>
          <ExampleGraph />
          <div className="survey-question">Do you really think that it is a fake news?</div>
          <div className="survey-options">
            <label className="survey-option">
              <input type="checkbox" checked={selected === 'yes'} onChange={() => handleSelect('yes')} /> <span>Yes</span>
            </label>
            <label className="survey-option">
              <input type="checkbox" checked={selected === 'no'} onChange={() => handleSelect('no')} /> <span>No</span>
            </label>
          </div>
          <div className="survey-actions">
            <button className="btn btn-primary" disabled={!selected} onClick={() => { handleNext(selected); onValidate(); }}>Finish</button>
          </div>
          <button className="btn btn-exit" onClick={onClose}>Exit</button>
        </div>
      </div>
    );
  }

  // Standard question modal
  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h3 className="modal-title">Community Survey</h3>
        <div className="survey-question">{surveyQuestions[step]}</div>
        <div className="survey-options">
          <label className="survey-option">
            <input type="checkbox" checked={selected === 'yes'} onChange={() => handleSelect('yes')} /> <span>Yes</span>
          </label>
          <label className="survey-option">
            <input type="checkbox" checked={selected === 'no'} onChange={() => handleSelect('no')} /> <span>No</span>
          </label>
        </div>
        <div className="survey-actions">
          <button className="btn btn-primary" disabled={!selected} onClick={() => handleNext(selected)}>Next</button>
        </div>
        <button className="btn btn-exit" onClick={onClose}>Exit</button>
      </div>
    </div>
  );
}

/**
 * Bloc d'information central : description tronquée, lien, sources et date
 */
const NewsInfoBlock = ({ description, link, sources, date }) => (
  <div className="news-info-block">
    {/* Description tronquée avec trois points si trop longue */}
    <p className="news-description" title={description}>{description}</p>
    {/* Lien de la fake news */}
    <a href={link} target="_blank" rel="noopener noreferrer" className="news-link">{link}</a>
    {/* Sources et date */}
    <div className="news-sources">
      {sources.map((src, idx) => (
        <div key={idx} className="news-source-item">
          <span className="news-date">{date}</span>
          <a href={src} target="_blank" rel="noopener noreferrer" className="news-source-link">{src}</a>
        </div>
      ))}
    </div>
  </div>
);

/**
 * Composant pour afficher une note sous forme d'étoiles
 */
function StarScore({ score, maxScore = 5 }) {
  return (
    <span className="star-score">
      {Array.from({ length: maxScore }).map((_, i) => (
        <span key={i} className={i < score ? 'star filled' : 'star'}>★</span>
      ))}
      <span className="score-text">{score}/{maxScore}</span>
    </span>
  );
}

/**
 * Bloc boutons verticaux, gère l'ouverture de la modale Report et l'affichage dynamique de la note
 */
const NewsActions = ({
  onReport,
  onCommunitySurvey,
  onTargetReport,
  onLastWordsAuthor,
  reportScore,
  showReportScore,
  communityScore,
  showCommunityScore,
  targetScore,
  showTargetScore,
  lastWordsScore,
  showLastWordsScore
}) => (
  <div className="news-actions">
    <div className="action-row">
      <button className="btn btn-report" onClick={onReport}>Report</button>
      {showReportScore && <StarScore score={reportScore} />}
    </div>
    <div className="action-row">
      <button className="btn btn-community" onClick={onCommunitySurvey}>Community Survey</button>
      {showCommunityScore && <StarScore score={communityScore} />}
    </div>
    <div className="action-row">
      <button className="btn btn-victim" onClick={onTargetReport}>Target Report</button>
      {showTargetScore && <StarScore score={targetScore} />}
    </div>
    <div className="action-row">
      <button className="btn btn-lastwords" onClick={onLastWordsAuthor}>Last Words Author</button>
      {showLastWordsScore && <StarScore score={lastWordsScore} />}
    </div>
    {/* Final grade only if all scores are revealed */}
    {(showReportScore && showCommunityScore && showTargetScore && showLastWordsScore) && (
      <div className="action-row">
        <button className="btn btn-finalgrade" disabled>Final grade</button>
        <StarScore
          score={(() => {
            const scores = [reportScore, communityScore, targetScore, lastWordsScore];
            const avg = scores.length ? scores.reduce((a, b) => a + b, 0) / scores.length : 0;
            return Math.round(avg);
          })()}
        />
      </div>
    )}
  </div>
);

// --- NewsCard modulaire ---
function NewsCard({ imageUrl, title, description, link, sources, date, ...props }) {
  // State pour chaque modale
  const [showReport, setShowReport] = useState(false);
  const [showSurvey, setShowSurvey] = useState(false);
  const [showTarget, setShowTarget] = useState(false);
  const [showAuthor, setShowAuthor] = useState(false);
  // State pour chaque score
  const [showReportScore, setShowReportScore] = useState(false);
  const [showCommunityScore, setShowCommunityScore] = useState(false);
  const [showTargetScore, setShowTargetScore] = useState(false);
  const [showLastWordsScore, setShowLastWordsScore] = useState(false);
  // Scores (peuvent être randomisés ou calculés)
  const reportScore = 2;
  const communityScore = 4;
  const targetScore = 3;
  const lastWordsScore = 1;
  const allScores = [reportScore, communityScore, targetScore, lastWordsScore];
  const finalGrade = Math.round(allScores.reduce((a, b) => a + b, 0) / allScores.length);
  // Affichage
  return (
    <div className="news-card">
      <img src={imageUrl} alt="news" className="news-image" />
      <div className="news-info-block">
        <h3>{title}</h3>
        <p className="news-description">{description}</p>
        <a href={link} target="_blank" rel="noopener noreferrer" className="news-link">{link}</a>
        <div className="news-sources">
          {sources && sources.map((src, idx) => (
            <div key={idx} className="news-source-item">
              <span className="news-date">{date}</span>
              <a href={src} target="_blank" rel="noopener noreferrer" className="news-source-link">{src}</a>
            </div>
          ))}
        </div>
      </div>
      <div className="news-actions">
        <div className="action-row">
          <button className="btn btn-report" onClick={() => setShowReport(true)}>Report</button>
          {showReportScore && <StarScore score={reportScore} />}
        </div>
        <div className="action-row">
          <button className="btn btn-community" onClick={() => setShowSurvey(true)}>Community Survey</button>
          {showCommunityScore && <StarScore score={communityScore} />}
        </div>
        <div className="action-row">
          <button className="btn btn-victim" onClick={() => setShowTarget(true)}>Target Report</button>
          {showTargetScore && <StarScore score={targetScore} />}
        </div>
        <div className="action-row">
          <button className="btn btn-lastwords" onClick={() => setShowAuthor(true)}>Last Words Author</button>
          {showLastWordsScore && <StarScore score={lastWordsScore} />}
        </div>
        {(showReportScore && showCommunityScore && showTargetScore && showLastWordsScore) && (
          <div className="action-row">
            <button className="btn btn-finalgrade" disabled>Final grade</button>
            <StarScore score={finalGrade} />
            <button className="btn btn-archive" onClick={props.onArchive} style={{marginLeft:16}}>Archiver la news</button>
          </div>
        )}
      </div>
      {showReport && <ReportModal onClose={() => setShowReport(false)} onValidate={() => { setShowReport(false); setShowReportScore(true); }} />}
      {showSurvey && <CommunitySurveyModal onClose={() => setShowSurvey(false)} onValidate={() => { setShowSurvey(false); setShowCommunityScore(true); }} />}
      {showTarget && <VictimReportModal onClose={() => setShowTarget(false)} onValidate={() => { setShowTarget(false); setShowTargetScore(true); }} />}
      {showAuthor && <LastWordsAuthorModal onClose={() => setShowAuthor(false)} onValidate={() => { setShowAuthor(false); setShowLastWordsScore(true); }} />}
    </div>
  );
}

// Utilitaire pour charger les news à traiter depuis localStorage
function getPendingNews() {
  return JSON.parse(window.localStorage.getItem('pendingNews') || '[]');
}
// Utilitaire pour archiver une news
function archiveNews(news, status) {
  const archive = JSON.parse(window.localStorage.getItem('archiveNews') || '[]');
  archive.push({ ...news, status });
  window.localStorage.setItem('archiveNews', JSON.stringify(archive));
}

export default function DashboardPage() {
  // State pour afficher les modales
  const [showReport, setShowReport] = useState(false);
  const [showSurvey, setShowSurvey] = useState(false);
  const [showTarget, setShowTarget] = useState(false);
  const [showAuthor, setShowAuthor] = useState(false);
  // State pour afficher la note Report après le premier clic
  const [showReportScore, setShowReportScore] = useState(false);
  const [showTargetScore, setShowTargetScore] = useState(false);
  const [showLastWordsScore, setShowLastWordsScore] = useState(false);
  const [showCommunityScore, setShowCommunityScore] = useState(false);
  // State pour la liste des news à traiter
  const [pendingNews, setPendingNews] = useState(getPendingNews());
  // State pour suivre les scores de chaque news (modulaire)
  const [scores, setScores] = useState({});

  // Exemple de score (peut être dynamique)
  const reportScore = 2;
  // ...autres scores statiques pour l'exemple...
  const communityScore = 4;
  const targetScore = 3;
  const lastWordsScore = 1;

  // Exemple de news unique
  const news = {
    imageUrl: '/assets/fake-news-example.jpg',
    description: "Cette fausse information a circulé massivement sur les réseaux sociaux en mai 2025. Elle a été partagée par de nombreux comptes influents et a généré beaucoup de réactions dans la communauté. Les vérifications ont montré qu'il s'agissait d'une manipulation.",
    link: 'https://www.example.com/fake-news',
    sources: [
      'https://www.source1.com/article',
      'https://www.source2.com/article'
    ],
    date: '17/05/2025',
  };

  // Nettoyer les news à traiter au démarrage
  React.useEffect(() => {
    window.localStorage.setItem('pendingNews', '[]');
  }, []);

  // Effet pour garder la liste à jour
  React.useEffect(() => {
    setPendingNews(getPendingNews());
  }, []);

  // Fonction pour mettre à jour les scores d'une news
  function updateScore(newsId, key, value) {
    setScores(prev => ({
      ...prev,
      [newsId]: {
        ...prev[newsId],
        [key]: value
      }
    }));
  }
  // Fonction pour savoir si tous les scores sont révélés pour une news
  function allScoresRevealed(newsId) {
    const s = scores[newsId] || {};
    return s.showReportScore && s.showCommunityScore && s.showTargetScore && s.showLastWordsScore;
  }
  // Fonction pour archiver et retirer la news du dashboard
  function handleArchive(news, status) {
    archiveNews(news, status);
    const updated = getPendingNews().filter(n => n.id !== news.id);
    window.localStorage.setItem('pendingNews', JSON.stringify(updated));
    setPendingNews(updated);
  }

  return (
    <div className="dashboard-page">
      <h2>Tableau de bord</h2>
      {pendingNews.length === 0 && <p>Toutes les news ont été vérifiées et archivées !</p>}
      {pendingNews.map(news => {
        const s = scores[news.id] || {};
        // Calcul de la moyenne pour le final grade
        const grades = [2, 4, 3, 1];
        const avg = Math.round(grades.reduce((a, b) => a + b, 0) / grades.length);
        return (
          <div key={news.id}>
            <NewsCard
              {...news}
              onReport={() => updateScore(news.id, 'showReportScore', true)}
              onCommunitySurvey={() => updateScore(news.id, 'showCommunityScore', true)}
              onTargetReport={() => updateScore(news.id, 'showTargetScore', true)}
              onLastWordsAuthor={() => updateScore(news.id, 'showLastWordsScore', true)}
              reportScore={2}
              showReportScore={!!s.showReportScore}
              communityScore={4}
              showCommunityScore={!!s.showCommunityScore}
              targetScore={3}
              showTargetScore={!!s.showTargetScore}
              lastWordsScore={1}
              showLastWordsScore={!!s.showLastWordsScore}
            />
            {allScoresRevealed(news.id) && (
              <div style={{marginBottom: 32}}>
                <button className="btn btn-finalgrade" disabled>Final grade</button>
                <span style={{marginLeft: 8}}>
                  <span className="star-score">
                    {Array.from({ length: 5 }).map((_, i) => (
                      <span key={i} className={i < avg ? 'star filled' : 'star'}>★</span>
                    ))}
                    <span className="score-text">{avg}/5</span>
                  </span>
                </span>
                <button className="btn btn-archive" onClick={() => handleArchive(news, avg < 3 ? 'fake' : 'real')} style={{marginLeft:16}}>Archiver la news</button>
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
}

