import React, { useState, useEffect } from 'react';
import './DashboardPage.css';

/**
 * Composant modulaire pour la fenÃªtre modale de report
 */
function ReportModal({ onClose }) {
  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h3 className="modal-title">Report</h3>
        <div className="modal-message">
          <p>
            Ce rapport signale cette news comme potentiellement fausse ou problÃ©matique. Merci de contribuer Ã  la fiabilitÃ© de la plateforme.
          </p>
        </div>
        <button className="btn btn-exit" onClick={onClose}>Exit</button>
      </div>
    </div>
  );
}

// --- Community Survey Modal ---
const surveyQuestions = [
  'Do you think this information is reliable?',
  'Have you seen this information elsewhere?',
  'Does this information impact your opinion?',
  // The last question will be handled separately for the graph
];

// Example graph component for the last question
function ExampleGraph() {
  return (
    <div className="survey-graph">
      <div className="bar-yes" style={{width: '70%'}}>Yes: 70%</div>
      <div className="bar-no" style={{width: '30%'}}>No: 30%</div>
    </div>
  );
}

function CommunitySurveyModal({ onClose }) {
  const [started, setStarted] = useState(false);
  const [step, setStep] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [selected, setSelected] = useState('');
  const [timer, setTimer] = useState(3600); // seconds
  const [timerRunning, setTimerRunning] = useState(false);
  const [score, setScore] = useState(null);

  // Persist timer state across rerenders (localStorage)
  useEffect(() => {
    const storedStart = window.localStorage.getItem('communityQuizStart');
    const storedScore = window.localStorage.getItem('communityQuizScore');
    if (storedStart) {
      const start = parseInt(storedStart, 10);
      const now = Date.now();
      const elapsed = Math.floor((now - start) / 1000);
      if (elapsed < 3600) {
        setStarted(true);
        setStep(4);
        setTimer(3600 - elapsed);
        setTimerRunning(true);
        setScore(storedScore ? parseInt(storedScore, 10) : null);
      } else {
        setStarted(true);
        setStep(4);
        setTimer(0);
        setTimerRunning(false);
        setScore(storedScore ? parseInt(storedScore, 10) : null);
      }
    }
  }, []);

  // Timer countdown
  useEffect(() => {
    if (timerRunning && timer > 0) {
      const interval = setInterval(() => {
        setTimer((prev) => {
          if (prev <= 1) {
            clearInterval(interval);
            setTimerRunning(false);
            // Generate and persist a random score
            const randomScore = Math.floor(Math.random() * 5) + 1;
            setScore(randomScore);
            window.localStorage.setItem('communityQuizScore', randomScore.toString());
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [timerRunning, timer]);

  // Start timer after last question
  useEffect(() => {
    if (step === 4 && !timerRunning && timer === 3600 && score === null) {
      setTimerRunning(true);
      const now = Date.now();
      window.localStorage.setItem('communityQuizStart', now.toString());
    }
  }, [step, timerRunning, timer, score]);

  // Start Quiz button
  if (!started) {
    return (
      <div className="modal-overlay">
        <div className="modal-content">
          <h3 className="modal-title">Community Survey</h3>
          <button className="btn btn-primary" onClick={() => setStarted(true)}>Start Quiz</button>
          <button className="btn btn-exit" onClick={onClose}>Exit</button>
        </div>
      </div>
    );
  }

  // Questions (only if not already answered)
  if (step < 3) {
    return (
      <div className="modal-overlay">
        <div className="modal-content">
          <h3 className="modal-title">Community Survey</h3>
          <div className="survey-question">{surveyQuestions[step]}</div>
          <div className="survey-options">
            <label className="survey-option">
              <input type="radio" name={`q${step}`} checked={selected === 'yes'} onChange={() => setSelected('yes')} /> <span>Yes</span>
            </label>
            <label className="survey-option">
              <input type="radio" name={`q${step}`} checked={selected === 'no'} onChange={() => setSelected('no')} /> <span>No</span>
            </label>
          </div>
          <div className="survey-actions">
            <button className="btn btn-primary" disabled={!selected} onClick={() => { setAnswers([...answers, selected]); setSelected(''); setStep(step + 1); }}>Next</button>
          </div>
          <button className="btn btn-exit" onClick={onClose}>Exit</button>
        </div>
      </div>
    );
  }
  // Last question with graph
  if (step === 3) {
    return (
      <div className="modal-overlay">
        <div className="modal-content">
          <h3 className="modal-title">Community Survey</h3>
          <ExampleGraph />
          <div className="survey-question">Do you really think that it is a fake news?</div>
          <div className="survey-options">
            <label className="survey-option">
              <input type="radio" name="q3" checked={selected === 'yes'} onChange={() => setSelected('yes')} /> <span>Yes</span>
            </label>
            <label className="survey-option">
              <input type="radio" name="q3" checked={selected === 'no'} onChange={() => setSelected('no')} /> <span>No</span>
            </label>
          </div>
          <div className="survey-actions">
            <button className="btn btn-primary" disabled={!selected} onClick={() => { setAnswers([...answers, selected]); setSelected(''); setStep(step + 1); }}>Finish</button>
          </div>
          <button className="btn btn-exit" onClick={onClose}>Exit</button>
        </div>
      </div>
    );
  }

  // Timer running
  if (timerRunning || (timer > 0 && step === 4 && score === null)) {
    const hours = Math.floor(timer / 3600);
    const minutes = Math.floor((timer % 3600) / 60);
    const seconds = timer % 60;
    return (
      <div className="modal-overlay">
        <div className="modal-content">
          <h3 className="modal-title">Community Survey</h3>
          <div className="modal-message">
            <p>Merci d'avoir rÃ©pondu Ã  l'enquÃªte communautaire !</p>
            <p>RÃ©sultats disponibles dans&nbsp;
              <b>{hours.toString().padStart(2, '0')}:{minutes.toString().padStart(2, '0')}:{seconds.toString().padStart(2, '0')}</b>
            </p>
          </div>
          <button className="btn btn-exit" onClick={onClose}>Exit</button>
        </div>
      </div>
    );
  }

  // Show score after timer
  if (score !== null && step === 4 && timer === 0) {
    return (
      <div className="modal-overlay">
        <div className="modal-content">
          <h3 className="modal-title">Community Survey</h3>
          <div className="modal-message">
            <p>Votre score :</p>
            <span className="star-score">
              {[...Array(score)].map((_, i) => <span key={i} className="star filled">â</span>)}
              {[...Array(5 - score)].map((_, i) => <span key={i} className="star">â</span>)}
              <span className="score-text">{score}/5</span>
            </span>
          </div>
          <button className="btn btn-exit" onClick={onClose}>Exit</button>
        </div>
      </div>
    );
  }
  return null;
}

/**
 * Bloc d'information central : description tronquÃ©e, lien, sources et date
 */
const NewsInfoBlock = ({ description, link, sources, date }) => (
  <div className="news-info-block">
    {/* Description tronquÃ©e avec trois points si trop longue */}
    <p className="news-description" title={description}>{description}</p>
    {/* Lien de la fake news */}
    <a href={link} target="_blank" rel="noopener noreferrer" className="news-link">{link}</a>
    {/* Sources et date */}
    <div className="news-sources">
      {sources.map((src, idx) => (
        <div key={idx} className="news-source-item">
          <span className="news-date">{date}</span>
          <a href={src} target="_blank" rel="noopener noreferrer" className="news-source-link">{src}</a>
        </div>
      ))}
    </div>
  </div>
);

/**
 * Composant pour afficher une note sous forme d'Ã©toiles
 */
function StarScore({ score, maxScore = 5 }) {
  return (
    <span className="star-score">
      {Array.from({ length: maxScore }).map((_, i) => (
        <span key={i} className={i < score ? 'star filled' : 'star'}>â</span>
      ))}
      <span className="score-text">{score}/{maxScore}</span>
    </span>
  );
}

/**
 * Bloc boutons verticaux, gÃ¨re l'ouverture de la modale Report et l'affichage dynamique de la note
 */
const NewsActions = ({
  onReport,
  onCommunitySurvey,
  onTargetReport,
  onLastWordsAuthor,
  reportScore,
  showReportScore,
  communityScore,
  showCommunityScore,
  targetScore,
  showTargetScore,
  lastWordsScore,
  showLastWordsScore
}) => (
  <div className="news-actions">
    <div className="action-row">
      <button className="btn btn-report" onClick={onReport}>Report</button>
      {showReportScore && <StarScore score={reportScore} />}
    </div>
    <div className="action-row">
      <button className="btn btn-community" onClick={onCommunitySurvey}>Community Survey</button>
      {showCommunityScore && <StarScore score={communityScore} />}
    </div>
    <div className="action-row">
      <button className="btn btn-victim" onClick={onTargetReport}>Target Report</button>
      {showTargetScore && <StarScore score={targetScore} />}
    </div>
    <div className="action-row">
      <button className="btn btn-lastwords" onClick={onLastWordsAuthor}>Last Words Author</button>
      {showLastWordsScore && <StarScore score={lastWordsScore} />}
    </div>
  </div>
);

/**
 * Composant NewsCard : image | bloc info | boutons
 */
const NewsCard = ({ imageUrl, description, link, sources, date, ...actionsProps }) => (
  <div className="news-card">
    {/* Bloc image Ã  gauche */}
    <img src={imageUrl} alt="news" className="news-image" />
    {/* Bloc central : description, lien, sources, date */}
    <NewsInfoBlock description={description} link={link} sources={sources} date={date} />
    {/* Bloc boutons Ã  droite */}
    <NewsActions {...actionsProps} />
  </div>
);

// Page Tableau de bord : affiche une news fake
export default function DashboardPage() {
  // State pour afficher les modales
  const [showReport, setShowReport] = useState(false);
  const [showSurvey, setShowSurvey] = useState(false);
  const [showTarget, setShowTarget] = useState(false);
  const [showAuthor, setShowAuthor] = useState(false);
  // State pour afficher la note Report aprÃ¨s le premier clic
  const [showReportScore, setShowReportScore] = useState(false);
  // Exemple de score (peut Ãªtre dynamique)
  const reportScore = 2;
  // ...autres scores statiques pour l'exemple...
  const communityScore = 4;
  const targetScore = 3;
  const lastWordsScore = 1;

  // Exemple de news unique
  const news = {
    imageUrl: '/assets/fake-news-example.jpg',
    description: "Cette fausse information a circulÃ© massivement sur les rÃ©seaux sociaux en mai 2025. Elle a Ã©tÃ© partagÃ©e par de nombreux comptes influents et a gÃ©nÃ©rÃ© beaucoup de rÃ©actions dans la communautÃ©. Les vÃ©rifications ont montrÃ© qu'il s'agissait d'une manipulation.",
    link: 'https://www.example.com/fake-news',
    sources: [
      'https://www.source1.com/article',
      'https://www.source2.com/article'
    ],
    date: '17/05/2025',
  };

  return (
    <div className="dashboard-page">
      <h2>Tableau de bord</h2>
      <NewsCard
        {...news}
        onReport={() => { setShowReport(true); }}
        onCommunitySurvey={() => setShowSurvey(true)}
        onTargetReport={() => setShowTarget(true)}
        onLastWordsAuthor={() => setShowAuthor(true)}
        reportScore={reportScore}
        showReportScore={showReportScore}
        communityScore={communityScore}
        showCommunityScore={true}
        targetScore={targetScore}
        showTargetScore={true}
        lastWordsScore={lastWordsScore}
        showLastWordsScore={true}
      />
      {showReport && <ReportModal onClose={() => { setShowReport(false); setShowReportScore(true); }} />}
      {showSurvey && <CommunitySurveyModal onClose={() => setShowSurvey(false)} />}
      {showTarget && <VictimReportModal onClose={() => setShowTarget(false)} />}
      {showAuthor && <LastWordsAuthorModal onClose={() => setShowAuthor(false)} />}
    </div>
  );
}

// --- Victim Report Modal ---
function VictimReportModal({ onClose }) {
  // Exemple de texte d'interview et vidÃ©o/screenshot
  const interviewText = `
    "Je tiens Ã  clarifier que les informations diffusÃ©es Ã  mon sujet sont totalement fausses. J'ai pris la parole publiquement pour rÃ©tablir la vÃ©ritÃ© et je remercie tous ceux qui m'ont soutenu durant cette pÃ©riode difficile."
  `;
  const victimMedia = '/assets/victim-interview.jpg'; // Remplacer par une vidÃ©o ou une image rÃ©elle si besoin

  return (
    <div className="modal-overlay">
      <div className="modal-content victim-modal">
        <h3 className="modal-title">Victim Report</h3>
        <div className="victim-modal-body">
          <div className="victim-interview-text">
            {interviewText}
          </div>
          <div className="victim-media">
            <img src={victimMedia} alt="Victim public statement" />
          </div>
        </div>
        <button className="btn btn-exit" onClick={onClose}>Exit</button>
      </div>
    </div>
  );
}

// --- Last Words Author Modal ---
function LastWordsAuthorModal({ onClose }) {
  // Exemple de texte et image de l'auteur de la fake news
  const authorText = `
    "Nous avons publiÃ© cette information sans vÃ©rifier sa vÃ©racitÃ©. Nous regrettons l'impact que cela a pu avoir et prÃ©sentons nos excuses Ã  la personne concernÃ©e."
  `;
  const authorMedia = '/assets/author-statement.jpg'; // Remplacer par une image rÃ©elle si besoin

  return (
    <div className="modal-overlay">
      <div className="modal-content author-modal">
        <h3 className="modal-title">Last Words Author</h3>
        <div className="author-modal-body">
          <div className="author-text">
            {authorText}
          </div>
          <div className="author-media">
            <img src={authorMedia} alt="Author public statement" />
          </div>
        </div>
        <button className="btn btn-exit" onClick={onClose}>Exit</button>
      </div>
    </div>
  );
}
