import React, { useState } from 'react';
import './DashboardPage.css';

/**
 * Composant modulaire pour la fenêtre modale de report
 */
function ReportModal({ onClose }) {
  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h3 className="modal-title">Report</h3>
        <div className="modal-message">
          {/* Message de rapport stylisé */}
          <p>
            Ce rapport signale cette news comme potentiellement fausse ou problématique. Merci de contribuer à la fiabilité de la plateforme.
          </p>
        </div>
        <button className="btn btn-exit" onClick={onClose}>Exit</button>
      </div>
    </div>
  );
}

/**
 * --- Community Survey Modal ---
 */
const surveyQuestions = [
  'Pensez-vous que cette information est fiable ?',
  'Avez-vous déjà vu cette information ailleurs ?',
  'Cette information a-t-elle un impact sur votre opinion ?',
  'Voici les résultats de la communauté :',
];

function CommunitySurveyModal({ onClose }) {
  // State pour suivre la question courante et les réponses
  const [step, setStep] = useState(0);
  const [answers, setAnswers] = useState([]);

  // Simule des données pour le graphique (question 4)
  const fakeGraphData = { yes: 68, no: 32 };

  // Gestion du clic sur Yes/No
  const handleAnswer = (answer) => {
    const newAnswers = [...answers, answer];
    setAnswers(newAnswers);
    if (step < surveyQuestions.length - 1) {
      setStep(step + 1);
    } else {
      // Optionnel : envoyer les réponses à un backend ici
      onClose();
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h3 className="modal-title">Community Survey</h3>
        {/* Affiche un graphique pour la dernière question */}
        {step === 3 && (
          <div className="survey-graph">
            <div className="bar-yes" style={{width: fakeGraphData.yes + '%'}}>
              Yes: {fakeGraphData.yes}%
            </div>
            <div className="bar-no" style={{width: fakeGraphData.no + '%'}}>
              No: {fakeGraphData.no}%
            </div>
          </div>
        )}
        <div className="survey-question">
          {step === 3 ? 'Pensez-vous que la communauté a bien jugé cette information ?' : surveyQuestions[step]}
        </div>
        <div className="survey-options">
          <label className="survey-option">
            <input type="checkbox" checked={false} readOnly /> Yes
          </label>
          <label className="survey-option">
            <input type="checkbox" checked={false} readOnly /> No
          </label>
        </div>
        <div className="survey-actions">
          <button className="btn btn-primary" onClick={() => handleAnswer('yes')}>Yes</button>
          <button className="btn btn-primary" onClick={() => handleAnswer('no')}>No</button>
        </div>
        <button className="btn btn-exit" onClick={onClose}>Exit</button>
      </div>
    </div>
  );
}

/**
 * Bloc d'information central : description tronquée, lien, sources et date
 */
const NewsInfoBlock = ({ description, link, sources, date }) => (
  <div className="news-info-block">
    {/* Description tronquée avec trois points si trop longue */}
    <p className="news-description" title={description}>{description}</p>
    {/* Lien de la fake news */}
    <a href={link} target="_blank" rel="noopener noreferrer" className="news-link">{link}</a>
    {/* Sources et date */}
    <div className="news-sources">
      {sources.map((src, idx) => (
        <div key={idx} className="news-source-item">
          <span className="news-date">{date}</span>
          <a href={src} target="_blank" rel="noopener noreferrer" className="news-source-link">{src}</a>
        </div>
      ))}
    </div>
  </div>
);

/**
 * Bloc boutons verticaux, gère l'ouverture de la modale Report
 */
const NewsActions = ({ onReport, onCommunitySurvey }) => (
  <div className="news-actions">
    <button className="btn btn-report" onClick={onReport}>Report</button>
    <button className="btn btn-community" onClick={onCommunitySurvey}>Community Survey</button>
    <button className="btn btn-victim">Victim Report</button>
    <button className="btn btn-lastwords">Last Words Author</button>
  </div>
);

/**
 * Composant NewsCard : image | bloc info | boutons
 */
const NewsCard = ({ imageUrl, description, link, sources, date, onReport, onCommunitySurvey }) => (
  <div className="news-card">
    {/* Bloc image à gauche */}
    <img src={imageUrl} alt="news" className="news-image" />
    {/* Bloc central : description, lien, sources, date */}
    <NewsInfoBlock description={description} link={link} sources={sources} date={date} />
    {/* Bloc boutons à droite */}
    <NewsActions onReport={onReport} onCommunitySurvey={onCommunitySurvey} />
  </div>
);

// Page Tableau de bord : affiche une news fake
export default function DashboardPage() {
  // State pour afficher les modales
  const [showReport, setShowReport] = useState(false);
  const [showSurvey, setShowSurvey] = useState(false);

  // Exemple de news unique
  const news = {
    imageUrl: '/assets/fake-news-example.jpg',
    description: "Cette fausse information a circulé massivement sur les réseaux sociaux en mai 2025. Elle a été partagée par de nombreux comptes influents et a généré beaucoup de réactions dans la communauté. Les vérifications ont montré qu'il s'agissait d'une manipulation.",
    link: 'https://www.example.com/fake-news',
    sources: [
      'https://www.source1.com/article',
      'https://www.source2.com/article'
    ],
    date: '17/05/2025',
  };

  return (
    <div className="dashboard-page">
      <h2>Tableau de bord</h2>
      <NewsCard
        {...news}
        onReport={() => setShowReport(true)}
        onCommunitySurvey={() => setShowSurvey(true)}
      />
      {showReport && <ReportModal onClose={() => setShowReport(false)} />}
      {showSurvey && <CommunitySurveyModal onClose={() => setShowSurvey(false)} />}
    </div>
  );
}
